import React from 'react';
import { Link, useParams } from 'react-router-dom';

// Utilitaire pour importer dynamiquement les fichiers JSON de recettes
function importAllRecettes() {
  const recettesContext = require.context('../datas/recettes', false, /\.json$/);
  const recettesFiles = recettesContext.keys();

  const allRecettes = {};

  recettesFiles.forEach((file) => {
    const recettes = recettesContext(file);
    Object.keys(recettes).forEach((legume) => {
      allRecettes[legume] = [...(allRecettes[legume] || []), ...recettes[legume]];
    });
  });

  return allRecettes;
}

// Importez toutes les recettes
const allRecettes = importAllRecettes();

function AfficherRecettes() {
  const { legume } = useParams();
  const recettesLegume = allRecettes[legume] || [];

  return (
    <div>
      <h2>Retrouvez ci-dessous toutes nos recettes pour {legume}</h2>
      {recettesLegume.map((recette) => (
        <div key={recette.id} className="recette__item">
          <Link to={`/recettes/${legume}`}>
            <div className="recette__content">
              <h4>{recette.nom}</h4>
              <img src={recette.image} alt={recette.nom} />
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
}

export default AfficherRecettes;
