import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPrint, faThumbsUp, faShareAlt } from '@fortawesome/free-solid-svg-icons';
import recettes from '../datas/recettes.json';
import '../styles/AfficherRecettes.css';

// Fonction pour partager la recette sur Facebook
const partagerSurFacebook = () => {
  // URL de votre site web
  const url = window.location.href; // URL actuelle de la page

  // URL de partage Facebook avec l'URL de votre site web
  const facebookShareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`;

  // Ouvrir une nouvelle fenêtre pour partager sur Facebook
  window.open(facebookShareUrl, '_blank', 'width=600,height=400');
};

function AfficherRecettes() {
  return (
    <div>
      <h2>Retrouvez ci-dessous toutes nos recettes</h2>
      {Object.entries(recettes).map(([legume, recettesLegume]) => (
        <div key={legume}>
          <h3>{legume} : nos recettes</h3>
          <div className="liste__recettes">
            {recettesLegume.map((recette) => (
              <div key={recette.id} className="recette__item">
                <Link to={`/recettes/${legume}`}>
                  <div className="recette__content">
                    <h4>{recette.nom}</h4>
                    <img src={recette.image} alt={recette.nom} />
                  </div>
                </Link>
                <div className="recette__actions">
                  {/* Icône pour imprimer la recette */}
                  <FontAwesomeIcon icon={faPrint} className="icon" onClick={() => window.print()} />
                  {/* Icône pour liker la recette */}
                  <FontAwesomeIcon icon={faThumbsUp} className="icon" />
                  {/* Icône pour partager sur Facebook */}
                  <FontAwesomeIcon icon={faShareAlt} className="icon" onClick={partagerSurFacebook} />
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default AfficherRecettes;
