import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import About from './components/About';
import NotFound from './components/NotFound';
import FichePlante from './components/FichePlante';
import Recettes from './components/Recettes';
import AfficherRecettes from './components/AfficherRecettes';

function App() {
  // Fonction de transformation du nom du légume pour l'URL
  const formatNomLien = (nom) => {
    return nom.toLowerCase().replace(/\s+/g, '-');
  };

  return (
    <Router>
      <Helmet>
        <title>Plantes et Potager</title>
      </Helmet>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        {/* Utilisation de la fonction de transformation dans le chemin */}
        <Route
          path="/fiche/:nom"
          element={<FichePlante />}
          transformPath={(params) => `/fiche/${formatNomLien(params.nom)}.html`}
        />
        <Route path="/recettes" element={<AfficherRecettes />} />
        <Route path="/recettes/:plante" element={<Recettes />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
